import React from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();

  // Get user from localStorage
  const user = JSON.parse(localStorage.getItem('user'));

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={styles.web}>
      <nav style={styles.nav}>
        <h2 style={styles.logo} onClick={() => navigate('/')}>CRUD <span class="white">App</span></h2>

        <div style={styles.links}>
          {!user && (
            <>
              <button style={styles.btn} onClick={() => navigate('/login')}>Login</button>
              <button style={styles.btn} onClick={() => navigate('/register')}>Register</button>
            </>
          )}

          {user && (
            <>
              {(user.role === 'admin' || user.role === 'manager') && (
                <button style={styles.btn} onClick={() => navigate('/products')}>Products</button>
              )}
              <span style={styles.userText}>
                Welcome, <strong>{user.username}</strong> ({user.role})
              </span>
              <button style={styles.btn} onClick={handleLogout}>Logout</button>
            </>
          )}
        </div>
      </nav>
    </div>
  );
};

const styles = {
  nav: {
    padding: '10px 20px',
    background: 'linear-gradient(100deg, #4e085f,#153677)',
    color: 'Black',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  logo: {
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '35px',
  },
  links: {
    display: 'flex',
    gap: '10px',
    alignItems: 'center',
  },
  btn: {
    padding: '6px 12px',
    backgroundColor: 'black',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    color: 'white',
    fontWeight: 'bold',
  },
  userText: {
    color: '#ddd',
  },
  web:{
    backgroundColor:'black'

  }
};

export default Navbar;
